INSERT INTO estilo_item (id, titulo, tipo, orden, texto) VALUES ('principios', 'Principios del periodista', 'regla', 1, 'Compromiso con la verdad; evitar ventajas personales; prohibido plagio, difamación y acusación sin pruebas; formación permanente.');
INSERT INTO estilo_item (id, titulo, tipo, orden, texto) VALUES ('atribuciones-fuentes', 'Atribuciones y tratamiento de fuentes', 'regla', 2, 'Atribuir con precisión desde la primera mención; evitar ''fuentes bien informadas''; cuando no se puede identificar, describir la condición de la fuente. Las citas textuales imprescindibles van entre comillas.');
INSERT INTO estilo_item (id, titulo, tipo, orden, texto) VALUES ('nombres-tratamientos', 'Nombres, tratamientos y siglas', 'regla', 3, 'Primera mención: cargo + nombre completo; posteriores: apellido. Evitar ''don'' y ''señor'' salvo casos consuetudinarios. Escribir siglas con el nombre completo primero.');
INSERT INTO estilo_item (id, titulo, tipo, orden, texto) VALUES ('lead-y-titulares', 'Lead y titulares', 'regla', 4, 'Título: síntesis sin adjetivos valorativos. Lead: 1-3 oraciones que respondan Qué/Quién/Dónde/Cuándo e incluyan atribución en el primer párrafo.');
INSERT INTO estilo_item (id, titulo, tipo, orden, texto) VALUES ('gramatica-puntuacion', 'Gramática y puntuación', 'regla', 5, 'No separar sujeto y predicado con coma; evitar gerundios de posterioridad; preferir voz activa; regla de dos puntos y mayúscula/minúscula.');
INSERT INTO estilo_item (id, titulo, tipo, orden, texto) VALUES ('proteger-menores', 'Protección de menores y temas sensibles', 'regla', 6, 'Prohibido publicar datos identificables de menores. Evitar detalles morbosos. Usar ''explotación sexual de menores'' en vez de ''prostitución infantil''.');
INSERT INTO estilo_item (id, titulo, tipo, orden, texto) VALUES ('web-reqs', 'Publicación web: requisitos mínimos', 'practica', 7, 'Título 40-70 chars; sumario ≤155 chars; foto de calidad; pie 80-150 chars; texto ≥2000 caracteres salvo nota en desarrollo; tags y autor.');